import React, { useState, useEffect } from 'react';
import api from '../../services/api';

const GenerateInvoice = () => {
  const [users, setUsers] = useState([]);
  const [selectedUserId, setSelectedUserId] = useState('');
  const [serviceCharge, setServiceCharge] = useState(0);
  const [platformFee, setPlatformFee] = useState(0);

  useEffect(() => {
    // Function to fetch users
    const fetchUsers = async () => {
      try {
        const response = await api.fetchUsers();
        setUsers(response);
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    // Initial fetch
    fetchUsers();

    // Polling every 5 seconds
    const intervalId = setInterval(fetchUsers, 5000);

    // Cleanup on component unmount
    return () => clearInterval(intervalId);
  }, []);

  const handleGenerateInvoice = async (event) => {
    event.preventDefault();
    try {
      const invoiceData = { userId: selectedUserId, serviceCharge, platformFee };
      const response = await api.generateInvoice(invoiceData);
      console.log('Invoice generated:', typeof(response));
      api.downloadInvoice(response);
      // Trigger PDF download
    //   if (response) {
    //     const downloadLink = document.createElement('a');
    //     downloadLink.href = response;
    //     downloadLink.setAttribute('download', `invoice_${selectedUserId}.pdf`);
    //     downloadLink.click();
    //   } else {
    //     console.error('No file path returned from the API');
    //   }
    } catch (error) {
      console.error('Error generating invoice:', error);
    }
  };

  return (
    <div>
      <h2>Generate Invoice</h2>
      <form onSubmit={handleGenerateInvoice}>
        <select value={selectedUserId} onChange={(e) => setSelectedUserId(e.target.value)} required>
          <option value="" disabled>Select User</option>
          {users.map((user) => (
            <option key={user._id} value={user._id}>{user.firstName} {user.lastName}</option>
          ))}
        </select>
        <input
          type="number"
          value={serviceCharge}
          onChange={(e) => setServiceCharge(e.target.value)}
          placeholder="Service Charge"
          required
        />
        <input
          type="number"
          value={platformFee}
          onChange={(e) => setPlatformFee(e.target.value)}
          placeholder="Platform Fee"
          required
        />
        <button type="submit">Generate Invoice</button>
      </form>
    </div>
  );
};

export default GenerateInvoice;
