import React, { useState } from 'react';
import api from '../../services/api';

const Register = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    phone: '',
    image: null, // To store the selected file
  });

  const handleChange = (e) => {
    if (e.target.name === 'image') {
      // Handle file input separately
      setFormData({ ...formData, image: e.target.files[0] }); // Store the first file selected
    } else {
      // Handle other inputs
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Create a FormData object
      const formDataToSubmit = new FormData();
      formDataToSubmit.append('firstName', formData.firstName);
      formDataToSubmit.append('lastName', formData.lastName);
      formDataToSubmit.append('email', formData.email);
      formDataToSubmit.append('password', formData.password);
      formDataToSubmit.append('phone', formData.phone);
      formDataToSubmit.append('image', formData.image); // Append the image file

      const response = await api.registerUser(formDataToSubmit);
      console.log('User registered:', response);
      // Optionally, redirect or show success message
    } catch (error) {
      console.error('Error registering user:', error);
    }
  };

  return (
    <div>
      <h2>Register</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="firstName"
          value={formData.firstName}
          onChange={handleChange}
          placeholder="First Name"
          required
        />
        <input
          type="text"
          name="lastName"
          value={formData.lastName}
          onChange={handleChange}
          placeholder="Last Name"
          required
        />
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          placeholder="Email"
          required
        />
        <input
          type="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          placeholder="Password"
          required
        />
        <input
          type="text"
          name="phone"
          value={formData.phone}
          onChange={handleChange}
          placeholder="Phone"
          required
        />
        <input
          type="file" // Input type for file upload
          name="image"
          onChange={handleChange}
          accept="image/*" // Accept only image files
          required
        />
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Register;
