import React, { useState } from 'react';
import axios from '../../services/api';

const GenerateInvoice = ({ userId }) => {
  const [serviceCharge, setServiceCharge] = useState(0);
  const [platformFee, setPlatformFee] = useState(0);

  const handleGenerateInvoice = async () => {
    try {
      const response = await axios.post('/invoices/generate', { userId, serviceCharge, platformFee });
      console.log('Invoice generated:', response.data);
      // Optionally, show success message or download PDF
    } catch (error) {
      console.error('Error generating invoice:', error);
      // Handle error, show an error message
    }
  };

  return (
    <div>
      <h2>Generate Invoice</h2>
      <form onSubmit={handleGenerateInvoice}>
        <input type="number" value={serviceCharge} onChange={(e) => setServiceCharge(e.target.value)} placeholder="Service Charge" required />
        <input type="number" value={platformFee} onChange={(e) => setPlatformFee(e.target.value)} placeholder="Platform Fee" required />
        <button type="submit">Generate Invoice</button>
      </form>
    </div>
  );
};

export default GenerateInvoice;
