import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

const fetchUsers = async () => {
  const response = await axios.get(`${API_BASE_URL}/users/user`);
  return response.data;
};

const generateInvoice = async (invoiceData) => {
  const response = await axios.post(`${API_BASE_URL}/invoices/generate`, invoiceData);
  return response.data;
};
const registerUser = async (userData) => {
    const response = await axios.post(`${API_BASE_URL}/invoices/generate`, userData);
    return response.data;
  };
const downloadInvoice = async (filePath) => {
  const link = document.createElement('a');
  link.href = `${API_BASE_URL}/invoices/download/${filePath}`;
  link.setAttribute('download', 'invoice.pdf');
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
};

export default {
  fetchUsers,
  generateInvoice,
  downloadInvoice,
  registerUser
};
